mutation {
    addDayDish (dayDish: {
        day: "2022-11-29",
        userCode: "290b5177-5f78-40cc-9bf7-888416fed988",
        dish: {
            name: "my dish"
        }
    }) {
        day,
        userCode,
        dish {
            id,
            name
        }
    }
}

