query {
    plan (dateFrom: "2022-11-20", dateTo: "2022-12-20", userCode: "290b5177-5f78-40cc-9bf7-888416fed988") {
        day,
        userCode,
        dishId,
        dish {
            name,
            description,
            url,
            ingredients {
